import 'package:device_setting/device_setting.dart';import 'package:flutter/foundation.dart';import 'package:flutter/material.dart';class Home extends StatefulWidget {  const Home({Key? key}) : super(key: key);  @override  State<Home> createState() => _HomeState();}class _HomeState extends State<Home> {  @override  Widget build(BuildContext context) {    const SettingName hotspotSetting = SettingName.passwordSetting;    return Scaffold(      appBar: AppBar(        title: const Text('Device Setting'),      ),      body: Center(        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          children: <Widget>[            InkWell(              child: Text(requestType(settingName: hotspotSetting)),              onTap: () {                if (kDebugMode) {                  print("Opening Device Setting");                  openDeviceSetting(settingName: hotspotSetting);                }              },            ),                        Card(              child: InkWell(                child: const Text("Open Location Setting"),                onTap: () {                  if (kDebugMode) {                    openDeviceSetting(settingName: SettingName.locationSetting);                  }                },              ),            ),          ],        ),      ),    );  }}